INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3174','PRC','3.2.07 - Nomina pubblico ufficiale per inventario','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3174" typecode="PRC" typedescr="Procedura"><descr>3.2.07 - Nomina pubblico ufficiale per inventario</descr><groups mainGroup="free" /><categories><category id="area_civile_ered_e_succ" /></categories><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.2.07 - Nomina pubblico ufficiale per inventario</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>In seguito all&rsquo;accettazione di un&rsquo;eredit&agrave; con il beneficio di inventario, al fine di procedere all&rsquo;inventario dei beni, nel caso in cui non sia stato gi&agrave; designato nel testamento del defunto, entro tre mesi dalla dichiarazione di accettazione deve essere nominato un Pubblico Ufficiale che svolga le operazioni di inventario.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Gli eredi della persona deceduta; in caso di minori sono previste particolari procedure ai sensi della legge.</p>
]]></hypertext></attribute><attribute name="DOVE_SI_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Autorit&agrave; competente: Cancelliere o Notaio. Ufficio Giudiziario: Tribunale o sezione distaccata di Tribunale, competente per territorio in relazione all&#39;ultimo domicilio del defunto.</p>
]]></hypertext></attribute><attribute name="DOC_NECESS" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<ul>
	<li>Copia dell&rsquo;accettazione con beneficio di inventario</li>
	<li>Prova del pagamento del Contributo Unificato e delle spese forfetizzate di notifica salvo gli specifici casi di esenzione previsti dalla legge</li>
</ul>
]]></hypertext></attribute><attribute name="TEMPI" attributetype="Hypertext" /><attribute name="COSTI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<ul>
	<li>Contributo Unificato da &euro; 98,00 in marche da bollo</li>
	<li>Marca da bollo da &euro; 27,00 per spese forfetizzate di notifica</li>
	<li>a seguito dell&rsquo;inventario, il Giudice quantifica la parcella del Pubblico Ufficiale che &egrave; a carico delle parti</li>
	<li>Il verbale di inventario &egrave; soggetto all&rsquo;imposta di bollo e di registro secondo le tariffe previste dalla legge</li>
</ul>
]]></hypertext></attribute></attributes><status>PUBLIC</status><version>2.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><created>20190624163354</created><lastModified>20190625104808</lastModified></content>
