INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3178','PRC','3.2.08 - Accettazione incarico di esecutore testamentario','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3178" typecode="PRC" typedescr="Procedura"><descr>3.2.08 - Accettazione incarico di esecutore testamentario</descr><groups mainGroup="free" /><categories><category id="area_civile_ered_e_succ" /></categories><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.2.08 - Accettazione incarico di esecutore testamentario</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>&Egrave; l&rsquo;accettazione espressa di incarico di esecutore testamentario, necessaria affinch&eacute; l&rsquo;incarico diventi esecutivo. Chi fa testamento pu&ograve; nominare un esecutore testamentario che assicuri che siano esattamente eseguite le disposizioni di ultima volont&agrave; del defunto (di norma di competenza dell&rsquo;erede). Tale fattispecie si pu&ograve; verificare per esempio se i testatore non ha fiducia nell&rsquo;erede, soprattutto quando l&rsquo;interesse di quest&rsquo;ultimo &egrave; in contrasto con alcune disposizioni del testamento a carattere particolare o per la presenza di contrasti tra gli aventi diritto all&rsquo;eredit&agrave; o l&rsquo;esistenza di obiettive difficolt&agrave; dell&rsquo;esecutore delle volont&agrave; del testatore. Occorre che l&rsquo;esecutore accetti la nomina o la rifiuti con dichiarazione resa in Tribunale.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>L&rsquo;esecutore testamentario. &Egrave; competente il Tribunale del luogo nella cui giurisdizione si &egrave; aperta la successione (luogo dell&rsquo;ultimo domicilio del defunto).</p>
]]></hypertext></attribute><attribute name="DOVE_SI_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Cancelleria Volontaria Giurisdizione</p>
]]></hypertext></attribute><attribute name="DOC_NECESS" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>La nomina dell&rsquo;esecutore testamentario pu&ograve; essere fatta solo con l&rsquo;atto di ultima volont&agrave;. Il testatore pu&ograve; nominare anche pi&ugrave; di esecutore.</p>

<p>All&rsquo;accettazione occorre allegare la seguente documentazione:</p>

<ul>
	<li>autocertificazione data e luogo della morte;</li>
	<li>copia conforme del testamento con estremi di registrazione;</li>
	<li>codice fiscale del defunto e dell&rsquo;esecutore testamentario.</li>
</ul>

<p>L&rsquo;accettazione non pu&ograve; essere sottoposta a condizione n&eacute; a termine.</p>
]]></hypertext></attribute><attribute name="TEMPI" attributetype="Hypertext" /><attribute name="COSTI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<ul>
	<li>Contributo Unificato da &euro; 98,00 in marche da bollo</li>
	<li>Marca da bollo da &euro; 27,00 per diritti forfetizzati di notifica</li>
</ul>
