INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3298','PRC','3.6.08 - Ammissione tardiva dei crediti','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3298" typecode="PRC" typedescr="Procedura"><descr>3.6.08 - Ammissione tardiva dei crediti</descr><groups mainGroup="free" /><categories /><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.6.08 - Ammissione tardiva dei crediti</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Sono considerate tardive le domande depositate oltre il termine di 30 giorni prima dell&rsquo;udienza fissata per la verifica del passivo e non oltre quello di dodici mesi dal deposito del decreto di esecutivit&agrave; dello stato passivo.&nbsp;</p>

<p>Il procedimento di accertamento delle domande tardive, si svolge nelle stesse forme delle tempestive di cui all&rsquo;art. 95.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Tutti i creditori</p>
]]></hypertext></attribute><attribute name="DOVE_SI_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Cancelleria Fallimentare</p>
]]></hypertext></attribute><attribute name="DOC_NECESS" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>La domanda di ammissione al passivo deve essere indirizzata al Giudice delegato del fallimento dichiarato Tribunale e deve contenere:</p>

<ul>
	<li>nome e cognome del creditore;</li>
	<li>indicazione della somma;</li>
	<li>indicazione del titolo da cui il credito deriva;</li>
	<li>indicazione delle ragioni di prelazione;</li>
	<li>indicazione dei documenti giustificativi.</li>
</ul>
]]></hypertext></attribute><attribute name="TEMPI" attributetype="Hypertext" /><attribute name="COSTI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<ul>
	<li>Esente da imposta di bollo</li>
	<li>Contributo Unificato in relazione al valore del credito per cui si procede (vd. Tabella del Contributo Unificato)</li>
</ul>
]]></hypertext></attribute></attributes><status>PUBLIC</status><version>1.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><lastModified>20190625144835</lastModified></content>
