INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3319','PRC','3.3.10 - Ricorso A Tutela Del Coniuge Separato O Divorziato 	','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3319" typecode="PRC" typedescr="Procedura"><descr>3.3.10 - Ricorso A Tutela Del Coniuge Separato O Divorziato 	</descr><groups mainGroup="free" /><categories><category id="area_civile_famiglia" /></categories><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.3.10 - Ricorso A Tutela Del Coniuge Separato O Divorziato</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Qualora il coniuge obbligato non rispetti le condizioni economiche previste dalla separazione o dal divorzio, il coniuge avente diritto pu&ograve; chiedere al Tribunale un provvedimento per ottenere il pagamento diretto da parte del datore di lavoro (o del terzo che &egrave; tenuto periodicamente al pagamento di somme di denaro) del coniuge inadempiente oppure il sequestro dei beni del coniuge obbligato a versare l&rsquo;assegno.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Il coniuge avente diritto accompagnato da un Avvocato</p>
