INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3381','PRC','3.5.01 - Conversione Del Pignoramento (Immobiliare) 	','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3381" typecode="PRC" typedescr="Procedura"><descr>3.5.01 - Conversione Del Pignoramento (Immobiliare) 	</descr><groups mainGroup="free" /><categories><category id="area_civile_ese_mob_e_imm" /></categories><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.5.01 - Conversione Del Pignoramento (Immobiliare)</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Prima che sia disposta la vendita o l&rsquo;assegnazione, il debitore pu&ograve; chiedere di sostituire alle cose pignorate una somma di denaro pari, oltre alle spese di esecuzione, all&rsquo;importo dovuto al creditore pignorante e ai creditori intervenuti, comprensivo del capitale, degli interessi e delle spese.</p>

<p>&nbsp;</p>

<p>Unitamente all&rsquo;istanza deve essere depositata, a pena di inammissibilit&agrave;, una somma non inferiore a un quinto dell&rsquo;importo del credito per cui &egrave; stato eseguito il pignoramento e dei crediti dei creditori intervenuti indicati nei rispettivi atti di intervento, dedotti i versamenti effettuati di cui deve essere data prova documentale.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Il debitore</p>
]]></hypertext></attribute><attribute name="DOVE_SI_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Cancelleria Esecuzioni Immobiliari</p>
]]></hypertext></attribute><attribute name="DOC_NECESS" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Con un&rsquo;istanza da depositare presso la Cancelleria delle Esecuzioni Immobiliari del Tribunale di Marsala unitamente a una somma non inferiore a un quinto dell&rsquo;importo del credito.</p>
]]></hypertext></attribute><attribute name="TEMPI" attributetype="Hypertext" /><attribute name="COSTI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Esente</p>
]]></hypertext></attribute></attributes><status>PUBLIC</status><version>1.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><lastModified>20190625152451</lastModified></content>
