INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('PRC3385','PRC','3.5.02 - Istanza Di Vendita 	','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="PRC3385" typecode="PRC" typedescr="Procedura"><descr>3.5.02 - Istanza Di Vendita 	</descr><groups mainGroup="free" /><categories><category id="area_civile_ese_mob_e_imm" /></categories><attributes><attribute name="titolo" attributetype="Text"><text lang="it">3.5.02 - Istanza Di Vendita</text></attribute><attribute name="COSE" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>&Egrave; la richiesta, fatta al Giudice dell&rsquo;esecuzione, di vendita del bene pignorato che pu&ograve; essere depositata decorsi 10 giorni dal pignoramento e nel termine di 90 giorni dal suo compimento (artt. 497 e 501 c.p.c.).&nbsp;</p>

<p>&nbsp;</p>

<p>L&rsquo;inosservanza del termine stabilito per il deposito dell&rsquo;istanza di vendita determina l&rsquo;inefficacia del pignoramento e l&rsquo;estinzione del processo esecutivo.</p>
]]></hypertext></attribute><attribute name="CHI_PUO_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Il creditore (procedente o intervenuto) munito di titolo esecutivo</p>
]]></hypertext></attribute><attribute name="DOVE_SI_RI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Cancelleria Esecuzioni Immobiliari</p>
]]></hypertext></attribute><attribute name="DOC_NECESS" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>L&rsquo;istanza di vendita va presentata tramite ricorso da depositare presso la Cancelleria Esecuzioni Immobiliari presso il Tribunale di Marsala.</p>

<p>Entro 120 giorni dal deposito del ricorso il creditore che richiede la vendita deve provvedere ad allegare la seguente documentazione:</p>

<ul>
	<li>estratto tavolare;</li>
	<li>visura catastale;</li>
	<li>eventuale decreto tavolare relativo all&rsquo;annotazione del pignoramento immobiliare;</li>
	<li>l&rsquo;avviso ai creditori iscritti ex art. 498 c.p.c..</li>
</ul>
]]></hypertext></attribute><attribute name="TEMPI" attributetype="Hypertext" /><attribute name="COSTI" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<ul>
	<li>Contributo Unificato come da tabella allegata (salvo esenzioni)</li>
	<li>Marca da bollo da &euro; 8,00 per diritti forfettari di notifica</li>
</ul>
]]></hypertext></attribute></attributes><status>PUBLIC</status><version>1.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><lastModified>20190625152621</lastModified></content>
