INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3183,'admin','2019-06-24 16:56:23','/do/jacms/Content','saveAndApprove','descr=3.1.01 - Iscrizione a ruolo
