INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3188,'admin','2019-06-25 10:33:18','/do/jacms/Content','saveAndApprove','descr=3.1.04 - Richiesta rilascio copie (atti, sentenze, decreti ingiuntivi, ecc.)
__multiselect_extraGroupNames=
