INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3194,'admin','2019-06-25 10:42:58','/do/jacms/Content','saveAndApprove','descr=3.2.04 - Inventario
__multiselect_extraGroupNames=
