INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3195,'admin','2019-06-25 10:45:33','/do/jacms/Content','saveAndApprove','descr=3.2.05 - Certificato di eredità
__multiselect_extraGroupNames=
treeNodesToOpen=home
