INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3261,'admin','2019-06-25 12:34:30','/do/jacms/Content','saveAndApprove','descr=Chi Siamo
__multiselect_extraGroupNames=
