INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3307,'admin','2019-06-25 14:52:32','/do/jacms/Content','saveAndApprove','descr=3.7.01 - Ammortamento della cambiale - smarrita, sottratta o distrutta
__multiselect_extraGroupNames=
