INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3311,'admin','2019-06-25 14:55:15','/do/jacms/Content','saveAndApprove','descr=3.7.02 - Ammortamento buoni fruttiferi e libretti di risparmio – smarriti, sottratti o distrutti
__multiselect_extraGroupNames=
