INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3324,'admin','2019-06-25 15:01:03','/do/jacms/Content','saveAndApprove','descr=3.7.05 - Istanza di riabilitazione a seguito di protesti
__multiselect_extraGroupNames=
