INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3378,'admin','2019-06-25 15:22:09','/do/jacms/Content','saveAndApprove','descr=3.9.01 - Iscrizioni all’albo dei c.t.u. e dei periti
__multiselect_extraGroupNames=
