INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (2026,'admin','2019-05-14 12:55:08','/do/jacms/Content','saveAndApprove','descr=Aste Giudiziarie
__multiselect_extraGroupNames=
