INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3376,'admin','2019-06-25 15:20:48','/do/jacms/Content','saveAndApprove','descr=3.8.11 - Patrocinio a spese dello stato nel processo penale
__multiselect_extraGroupNames=
