INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3426,'admin','2019-06-25 16:09:48','/do/jacms/Content','saveAndApprove','descr=3.1.02 - Patrocinio a spese dello stato nel processo civile
__multiselect_extraGroupNames=
