INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3674,'admin','2019-07-19 20:06:26','/do/jacms/Content','saveAndApprove','descr=Chi Siamo
__multiselect_extraGroupNames=
