INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (3916,'admin','2019-07-21 17:48:09','/do/jacms/Content','saveAndApprove','descr=Il Tribunale
