INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (4255,'admin','2019-08-01 09:20:40','/do/jacms/Content','saveAndApprove','descr=Chi Siamo
__multiselect_extraGroupNames=
treeNodesToOpen=home
contentOnSessionMarker=CON_editContent_CON3156
