INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('CON1998','CON','Comunicazione esito vendita','PUBLIC','<?xml version="1.0" encoding="UTF-8"?>
<content id="CON1998" typecode="CON" typedescr="Contenuto composto"><descr>Comunicazione esito vendita</descr><groups mainGroup="free" /><categories /><attributes><list name="contComp" attributetype="Monolist" nestedtype="Composite"><composite name="contComp" attributetype="Composite"><attribute name="title" attributetype="Text" /><attribute name="text" attributetype="Hypertext"><hypertext lang="it"><![CDATA[<p>Sar&agrave; cura del richiedente comunicare l&#39;esito dell&rsquo;esperimento di vendita per poterlo annotare tra i dati relativi alla vendita sul sito. Nel caso di non aggiudicazione e fissazione di nuova data di vendita, la visibilit&agrave; dei dati relativi alla vendita appena passata verr&agrave; prolungata, senza ulteriore addebito, in attesa della documentazione relativa alla vendita successiva.<br />
Tale comunicazione potr&agrave; essere effettuata:<br />
<strong>&bull;</strong> aggiornando in tempo reale direttamente on line gli esiti delle vendite, seguendo le istruzioni riportate nella email ricevuta relativa alla richiesta di comunicazione di esito delle vendite;<br />
<strong>&bull;</strong> rispondendo alla email di richiesta esito inviata dal servizio il giorno fissato per la vendita;<br />
<strong>&bull;</strong> inviando via fax al numero 0586 20 14 31 oppure 0586 21 91 64 la email di richiesta esito ricevuta, dopo aver compilato gli appositi campi relativi all&#39;esito ed averla stampata;<br />
<strong>&bull;</strong> inviando via fax al numero 0586 20 14 31 oppure 0586 21 91 64 l&#39;apposito modulo denominato &quot;Esito delle vendite dei singoli lotti&quot;, ricevuto insieme al certificato di pubblicazione, per coloro che non abbiano un indirizzo di posta elettronica.<br />
In tal modo verranno evitate richieste d&#39;informazione agli uffici da parte degli utenti nei tempi immediatamente successivi alle vendite.<br />
Per ulteriori chiarimenti &egrave; possibile contattare il nostro staff al n&deg; 0586 20141.</p>
]]></hypertext></attribute><attribute name="img" attributetype="Image" /><attribute name="link" attributetype="Link"><properties /></attribute><attribute name="allegato" attributetype="Attach" /></composite></list></attributes><status>PUBLIC</status><version>2.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><created>20190508160653</created><lastModified>20190508161556</lastModified></content>
