INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (1782,'admin','2019-05-07 15:33:23','/do/jacms/Content','saveAndApprove','descr=descrizione trasparenza
__multiselect_extraGroupNames=
