INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (1863,'admin','2019-05-08 10:44:16','/do/jacms/Content','saveAndApprove','descr=Aste Giudiziarie
__multiselect_extraGroupNames=
