INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (1947,'admin','2019-05-08 14:50:32','/do/jacms/Content','saveAndApprove','descr=Caratteristiche del servizio
